    font-size: 1.5rem;
    font-weight: 700;
    margin-bottom: 0.5rem;
    text-shadow: 0 2px 4px rgba(0, 0, 0, 0.5);
  }

  .overlay-subtitle {
    color: rgba(255, 255, 255, 0.9);
    font-size: 1rem;
    text-shadow: 0 2px 4px rgba(0, 0, 0, 0.5);
  }

  .article-body {
    padding: 1.5rem;
  }

  .author-info {
    margin-bottom: 1rem;
  }

  .author-name {
    font-weight: 600;
    color: var(--text-color, #333);
  }

  .article-content {
    color: var(--text-color-secondary, #666);
