import { Component, Optional, Self } from '@angular/core';

import { FormControl, NgControl } from '@angular/forms';
import { CheckboxModule } from 'primeng/checkbox';
import { CheckboxConfig, CheckboxModeEnum } from '../models/checkbox-contract';
import { BaseFormControlComponent } from '../../base/base-form-control/base-form-control.component';
import { FormComponentVariantEnum } from '../../common/form-element-common';

@Component({
  selector: 'lib-base-checkbox',
  standalone: true,
  imports: [CheckboxModule],
  template: '', // Base component doesn't need a template
})
export class BaseCheckboxComponent extends BaseFormControlComponent<CheckboxConfig, boolean | any> {
  constructor(@Optional() @Self() public override ngControl: NgControl) {
    super(ngControl);
  }

  // For renamed property access to maintain backward compatibility
  get checkboxControl(): FormControl<any> {
    return this.formControl;
  }

  /**
   * Initialize the component with the default configuration
   */
  protected override initializeComponent(): void {
    // Set default config values
    this.config = {
      ...this.config,
      id: this.config?.id || `checkbox-${Math.random().toString(36).substring(2, 9)}`,
      mode: this.config?.mode || CheckboxModeEnum.BINARY,
      size: this.config?.size,
      variant: this.config?.variant || FormComponentVariantEnum.OUTLINED,
      required: this.config?.required || false,
      disabled: this.config?.disabled || false,
    };

    // Apply value to form control
    if (this.value !== undefined) {
      this.formControl.setValue(this.value);
    }

    // Apply disabled state through the FormControl's methods
    if (this.config.disabled) {
      this.formControl.disable({ emitEvent: false });
    } else {
      this.formControl.enable({ emitEvent: false });
    }
  }

  /**
   * Get a validation message for specific errors
   */
  protected override getValidationMessage(errors: any): string | null {
    // Return the custom error message if set
    if (this.config.errorMessage) {
      return this.config.errorMessage;
    }

    // Otherwise, return a message based on the error type
    if (errors['required']) {
      return 'This field is required';
    }

    // If no specific error message was found, return a generic message
    return 'Invalid selection';
  }

  /**
   * Set the disabled state in the config
   */
  protected override setDisabled(isDisabled: boolean): void {
    this.config.disabled = isDisabled;
  }
}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import {
  AbstractControl,
  FormBuilder,
  FormGroup,
  ReactiveFormsModule,
  Validators,
} from '@angular/forms';
import { ContactFormData } from '../models/contact-form-contract';

@Component({
  selector: 'lib-base-contact-form',
  standalone: true,
  imports: [ReactiveFormsModule],
  template: '', // Base component doesn't need a template
})
export class BaseContactFormComponent {
  @Input() title = 'Contact Us';
  @Input() submitButtonText = 'Send Message';
  @Input() isSubmitting = false;
  @Input() formError = '';
  @Input() formSuccess = '';

  /**
   * Emits form data when the form is submitted
   */
  @Output() submitContactForm: EventEmitter<ContactFormData> = new EventEmitter<ContactFormData>();

  contactForm: FormGroup;
  submitted = false;

  constructor(protected fb: FormBuilder) {
    this.contactForm = this.createContactForm();
  }

  /**
   * Getters for form controls to simplify access in templates
   */
  get name(): AbstractControl | null {
    return this.contactForm.get('name');
  }

  get email(): AbstractControl | null {
    return this.contactForm.get('email');
  }

  get phone(): AbstractControl | null {
    return this.contactForm.get('phone');
  }

  get subject(): AbstractControl | null {
    return this.contactForm.get('subject');
  }

  get message(): AbstractControl | null {
    return this.contactForm.get('message');
  }

  /**
   * Handles form submission
   */
  onSubmit(): void {
    this.submitted = true;

    if (this.contactForm.valid) {
      const formData: ContactFormData = {
        name: this.name?.value,
        email: this.email?.value,
        phone: this.phone?.value,
        subject: this.subject?.value,
        message: this.message?.value,
      };

      this.submitContactForm.emit(formData);
    } else {
      this.markAllFieldsAsTouched();
    }
  }

  /**
   * Validates a phone number pattern
   * @param control The form control to validate
   * @returns Validation result object or null if valid
   */
  phoneValidator(control: AbstractControl): { [key: string]: boolean } | null {
    if (!control.value) {
      return null; // Phone is optional
    }

    // Basic phone validation - can be customized based on requirements
    const isValid = /^\+?[\d\s()-]{7,20}$/.test(control.value);
    return isValid ? null : { invalidPhone: true };
  }

  /**
   * Marks all form fields as touched to trigger validation display
   */
  markAllFieldsAsTouched(): void {
    Object.keys(this.contactForm.controls).forEach((field: string): void => {
      const control: AbstractControl | null = this.contactForm.get(field);
      control?.markAsTouched();
    });
  }

  /**
   * Creates the contact form with appropriate validators
   * @returns A new FormGroup instance with validators
   */
  protected createContactForm(): FormGroup {
    return this.fb.group({
      name: ['', [Validators.required, Validators.minLength(2), Validators.maxLength(100)]],
      email: ['', [Validators.required, Validators.email]],
      phone: ['', [this.phoneValidator.bind(this)]],
      subject: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(100)]],
      message: ['', [Validators.required, Validators.minLength(10), Validators.maxLength(1000)]],
    });
  }
}

import { Component, Optional, Self } from '@angular/core';

import { FormControl, FormsModule, NgControl, ReactiveFormsModule } from '@angular/forms';
import { InputNumberModule } from 'primeng/inputnumber';
import { FloatLabelModule } from 'primeng/floatlabel';
import { IftaLabelModule } from 'primeng/iftalabel';
import { BaseFormControlComponent } from '../base/base-form-control/base-form-control.component';
import { CurrencyDisplayModeEnum, CurrencyInputConfig } from './models/currency-input-contract';
import { FormLabelStyleEnum, FormLabelPositionEnum } from '../common/form-element-common';

@Component({
  selector: 'lib-base-currency-input',
  standalone: true,
  imports: [
    FormsModule,
    ReactiveFormsModule,
    InputNumberModule,
    FloatLabelModule,
    IftaLabelModule
],
  template: '', // Base component doesn't need a template
})
export class BaseCurrencyInputComponent extends BaseFormControlComponent<
  CurrencyInputConfig,
  number
> {
  constructor(@Optional() @Self() public override ngControl: NgControl) {
    super(ngControl);
  }

  // For renamed property access
  get inputNumberControl(): FormControl<number | null> {
    return this.formControl;
  }

  /**
   * Initialize the component with the default configuration
   */
  protected override initializeComponent(): void {
    // Set default config values
    this.config = {
      ...this.config,
      id: this.config?.id || `currency-input-${Math.random().toString(36).substring(2, 9)}`,
      mode: 'currency',
      currency: this.config?.currency || 'USD',
      locale: this.config?.locale || 'en-US',
      currencyDisplay: this.config?.currencyDisplay || CurrencyDisplayModeEnum.SYMBOL,
      useGrouping: this.config?.useGrouping ?? true,
      showButtons: this.config?.showButtons ?? false,
      minFractionDigits: this.config?.minFractionDigits ?? 2,
      maxFractionDigits: this.config?.maxFractionDigits ?? 2,
      labelStyle: this.config?.labelStyle || FormLabelStyleEnum.DEFAULT,
      labelPosition: this.config?.labelPosition || FormLabelPositionEnum.ABOVE,
      required: this.config?.required ?? false,
