import { Component, EventEmitter, Input, Output, signal, WritableSignal } from '@angular/core';
import { FormBuilder, FormGroup, Validators, AbstractControl } from '@angular/forms';
import { RegisterFormData } from '../models/registration-contract';

@Component({
  selector: 'lib-base-registration',
  standalone: true,
  imports: [],
  templateUrl: './base-registration.component.html',
  styleUrl: './base-registration.component.scss',
})
export class BaseRegistrationComponent {
  @Input() isSubmitting = false;
  @Input() title = 'Create Account';
  @Input() registrationError = '';

  @Output() submitRegistration: EventEmitter<RegisterFormData> =
    new EventEmitter<RegisterFormData>();
  @Output() navigateToLoginRequest: EventEmitter<void> = new EventEmitter<void>();

  registrationForm: FormGroup;
  submitted = false;

  passwordStrength: WritableSignal<number> = signal<number>(0);
  showPassword: WritableSignal<boolean> = signal<boolean>(false);

  constructor(protected fb: FormBuilder) {
    this.registrationForm = this.createRegistrationForm();
  }

  /**
   * Form controls getters for easier access in templates
   */
  get username(): AbstractControl | null {
    return this.registrationForm.get('username');
  }

  get email(): AbstractControl | null {
    return this.registrationForm.get('email');
  }

  get password(): AbstractControl | null {
    return this.registrationForm.get('password');
  }

  get confirmPassword(): AbstractControl | null {
    return this.registrationForm.get('confirmPassword');
  }

  get acceptTerms(): AbstractControl | null {
    return this.registrationForm.get('acceptTerms');
  }

  /**
   * Validates that password and confirm password match
   * @param form Form group to validate
   * @returns Validation error or null if valid
   */
  passwordMatchValidator(form: FormGroup): { [key: string]: boolean } | null {
    const password = form.get('password')?.value;
    const confirmPassword = form.get('confirmPassword')?.value;
    return password !== confirmPassword ? { mismatch: true } : null;
  }

  /**
   * Submits the registration form if valid
   */
  onSubmit(): void {
    this.submitted = true;

    if (this.registrationForm.valid) {
      const formData: RegisterFormData = {
        username: this.registrationForm.value.username,
        email: this.registrationForm.value.email,
        password: this.registrationForm.value.password,
        acceptTerms: this.registrationForm.value.acceptTerms,
      };

      this.submitRegistration.emit(formData);
    } else {
      this.markAllFieldsAsTouched();
    }
  }

  /**
   * Calculates password strength (0-5)
   * Can be called from input-text component event handler
   * @param event The input event or value to check
   */
  checkPasswordStrength(event: Event | any): void {
    let password = '';

    // Handle different types of events
    if (typeof event === 'string') {
      // If directly passing a string
      password = event;
    } else if (event instanceof Event) {
      // Standard DOM event
      password = (event.target as HTMLInputElement).value;
    } else if (event?.detail?.value) {
      // CustomEvent from input-text component
      password = event.detail.value;
    }

    const strength: number = this.calculatePasswordStrength(password);
    this.passwordStrength.set(strength);
  }

  /**
   * Calculates password strength based on various criteria
   * @param password The password to evaluate
   * @returns A strength score from 0 to 5
   */
  calculatePasswordStrength(password: string): number {
    let strength = 0;
    if (password.length >= 8) strength++;
    if (/[a-z]/.test(password)) strength++;
    if (/[A-Z]/.test(password)) strength++;
    if (/[0-9]/.test(password)) strength++;
    if (/[^a-zA-Z0-9]/.test(password)) strength++;
    return strength;
  }

  /**
   * Returns a label describing password strength
   * @returns A text label based on the current strength
   */
  getPasswordStrengthLabel(): string {
    const labels: string[] = ['Very Weak', 'Weak', 'Fair', 'Good', 'Strong', 'Very Strong'];
    return labels[this.passwordStrength()] || 'Unknown';
  }

  /**
   * Returns a color representing password strength
   * @returns A color string based on the current strength
   */
  getPasswordStrengthColor(): string {
    const colors: string[] = ['red', 'orangered', 'orange', 'yellowgreen', 'green', 'darkgreen'];
    return colors[this.passwordStrength()] || 'gray';
  }

  /**
   * Toggles password visibility
   */
  togglePasswordVisibility(): void {
    this.showPassword.update((value: boolean): boolean => !value);
  }

  /**
   * Marks all form fields as touched to show validation errors
   */
  markAllFieldsAsTouched(): void {
    Object.keys(this.registrationForm.controls).forEach((field: string): void => {
      const control: AbstractControl | null = this.registrationForm.get(field);
      control?.markAsTouched();
    });
  }

  /**
   * Navigates to login page
   */
  navigateToLogin(): void {
    this.navigateToLoginRequest.emit();
  }

  /**
   * Creates the registration form with validation
   * @returns The initialized FormGroup
   */
  protected createRegistrationForm(): FormGroup {
    return this.fb.group(
      {
        username: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(30)]],
        email: ['', [Validators.required, Validators.email]],
        password: ['', [Validators.required, Validators.minLength(6)]],
        confirmPassword: ['', [Validators.required]],
        acceptTerms: [false, [Validators.requiredTrue]],
      },
      {
        validators: this.passwordMatchValidator,
      },
    );
  }
}

import { Component, EventEmitter, Input, Output, signal, WritableSignal } from '@angular/core';
import {
  AbstractControl,
  FormBuilder,
  FormGroup,
  ReactiveFormsModule,
  Validators,
} from '@angular/forms';
import { ResetPasswordFormData } from '../models/reset-password-contract';

@Component({
  selector: 'lib-base-reset-password',
  standalone: true,
  imports: [ReactiveFormsModule],
  template: '', // Base component doesn't need a template
})
export class BaseResetPasswordComponent {
  @Input() title = 'Reset Password';
  @Input() token = '';
  @Input() isSubmitting: WritableSignal<boolean> = signal(false);
  @Input() resetError = '';

  @Output() submitResetPassword: EventEmitter<ResetPasswordFormData> =
    new EventEmitter<ResetPasswordFormData>();
  @Output() navigateToLoginRequest: EventEmitter<void> = new EventEmitter<void>();
  @Output() navigateToForgotPasswordRequest: EventEmitter<void> = new EventEmitter<void>();

  resetPasswordForm: FormGroup;
  submitted = false;
  resetSuccessful: WritableSignal<boolean> = signal(false);
  tokenError: WritableSignal<boolean> = signal(false);
  passwordStrength: WritableSignal<number> = signal(0);
  showPassword: WritableSignal<boolean> = signal(false);

  constructor(protected fb: FormBuilder) {
    this.resetPasswordForm = this.createForm();
  }

  /**
   * Form controls getters for easier access in templates
   */
  get password(): AbstractControl | null {
    return this.resetPasswordForm.get('password');
  }

  get confirmPassword(): AbstractControl | null {
    return this.resetPasswordForm.get('confirmPassword');
  }

  /**
   * Handles form submission
   */
  onSubmit(): void {
    this.submitted = true;

    if (this.resetPasswordForm.valid) {
      const formData: ResetPasswordFormData = {
        password: this.password?.value,
        confirmPassword: this.confirmPassword?.value,
      };

      this.submitResetPassword.emit(formData);
    } else {
      this.markAllFieldsAsTouched();
    }
  }

  /**
   * Sets the reset successful state
   */
  setResetSuccessful(): void {
    this.resetSuccessful.set(true);
  }

  /**
   * Sets the token error state
   */
  setTokenError(): void {
    this.tokenError.set(true);
  }

  /**
   * Navigates to login page
   */
  navigateToLogin(): void {
    this.navigateToLoginRequest.emit();
  }

  /**
   * Navigates to forgot password page
   */
  navigateToForgotPassword(): void {
    this.navigateToForgotPasswordRequest.emit();
  }

  /**
   * Calculates password strength (0-5)
   */
  checkPasswordStrength(event: Event): void {
    const password: string = (event.target as HTMLInputElement).value;
    const strength: number = this.calculatePasswordStrength(password);
    this.passwordStrength.set(strength);
  }

  /**
   * Calculates password strength based on various criteria
   */
  calculatePasswordStrength(password: string): number {
    let strength = 0;
    if (password.length >= 8) strength++;
    if (/[a-z]/.test(password)) strength++;
    if (/[A-Z]/.test(password)) strength++;
    if (/[0-9]/.test(password)) strength++;
    if (/[^a-zA-Z0-9]/.test(password)) strength++;
    return strength;
  }

  /**
   * Returns a label describing password strength
   */
  getPasswordStrengthLabel(): string {
    const labels: string[] = ['Very Weak', 'Weak', 'Fair', 'Good', 'Strong', 'Very Strong'];
    return labels[this.passwordStrength()] || 'Unknown';
  }

  /**
   * Returns a color representing password strength
   */
  getPasswordStrengthColor(): string {
    const colors: string[] = ['red', 'orangered', 'orange', 'yellowgreen', 'green', 'darkgreen'];
    return colors[this.passwordStrength()] || 'gray';
  }

  /**
   * Toggles password visibility
   */
  togglePasswordVisibility(): void {
    this.showPassword.update((value: boolean): boolean => !value);
  }

  /**
   * Marks all form fields as touched to display validation errors
   */
  private markAllFieldsAsTouched(): void {
    Object.keys(this.resetPasswordForm.controls).forEach((field: string): void => {
      const control: AbstractControl | null = this.resetPasswordForm.get(field);
      control?.markAsTouched();
    });
  }

  /**
   * Creates the reset password form with validation
   */
  private createForm(): FormGroup {
    return this.fb.group(
      {
        password: ['', [Validators.required, Validators.minLength(6)]],
        confirmPassword: ['', [Validators.required]],
      },
      {
        validators: this.passwordMatchValidator,
      },
    );
  }

  /**
   * Validates that password and confirm password match
   */
  private passwordMatchValidator(group: FormGroup): { [key: string]: boolean } | null {
    const password = group.get('password')?.value;
    const confirmPassword = group.get('confirmPassword')?.value;
    return password !== confirmPassword ? { mismatch: true } : null;
  }
}

import {
  Component,
  EventEmitter,
  Input,
  OnChanges,
  OnInit,
  Output,
  SimpleChanges,
  ViewChild,
} from '@angular/core';

import { CardModule } from 'primeng/card';
import { FormsModule } from '@angular/forms';
import { SelectButtonModule } from 'primeng/selectbutton';

// Import ApexCharts
import {
  ApexAxisChartSeries,
  ApexChart,
  ApexXAxis,
  ApexDataLabels,
  ApexStroke,
  ApexYAxis,
  ApexLegend,
  ApexFill,
  ApexTooltip,
  ApexTitleSubtitle,
  ApexPlotOptions,
  ChartComponent,
  NgApexchartsModule,
} from 'ng-apexcharts';

import {
  MonthlySalesData,
  QuarterlySalesData,
  SalesChartData,
  TimePeriod,
} from '../models/sales-chart-widget-contract';

// Chart options type definition
export type ChartOptions = {
  series: ApexAxisChartSeries;
  chart: ApexChart;
  xaxis: ApexXAxis;
  yaxis: ApexYAxis;
  dataLabels: ApexDataLabels;
  stroke: ApexStroke;
  title: ApexTitleSubtitle;
  legend: ApexLegend;
  fill: ApexFill;
  tooltip: ApexTooltip;
  plotOptions: ApexPlotOptions;
  colors: string[];
};

/**
 * Base component for sales chart widgets that displays sales data
 * This component can be extended or used directly to display sales charts
 */
@Component({
  selector: 'lib-base-sales-chart-widget',
  standalone: true,
  imports: [CardModule, NgApexchartsModule, SelectButtonModule, FormsModule],
  templateUrl: './base-sales-chart-widget.component.html',
  styleUrls: ['./base-sales-chart-widget.component.scss'],
})
export class BaseSalesChartWidgetComponent implements OnInit, OnChanges {
  @ViewChild('chart') chart!: ChartComponent;
  public chartOptions!: ChartOptions;

  /**
   * Title for the chart widget
   */
  @Input() title = 'Sales Overview';

  /**
   * Description text for the widget
   */
  @Input() description = 'Performance by product category';

  /**
   * Whether the chart is in a loading state
   */
  @Input() loading = false;

  /**
   * Error message to display if data loading fails
   */
  @Input() error = '';

  /**
   * Height of the chart in pixels
   */
  @Input() height = 350;
  /**
   * Time period options for filtering
   */
  @Input() timePeriods: TimePeriod[] = [
    { name: 'Monthly', value: 'monthly' },
    { name: 'Quarterly', value: 'quarterly' },
  ];
  /**
   * Selected period for filtering
   */
  @Input() selectedPeriod = 'monthly';
  /**
   * Event emitted when the period changes
   */
  @Output() periodChange = new EventEmitter<{ value: string }>();

  /**
   * Preset chart data or data loaded from API
   * Using protected instead of private to allow subclasses to access it
   */
  protected _chartData: SalesChartData = {
    monthlySalesData: {
      months: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
      series: [
        {
          name: 'Software',
          data: [44, 55, 57, 56, 61, 58, 63, 60, 66, 68, 78, 74],
        },
        {
          name: 'Hardware',
          data: [76, 85, 101, 98, 87, 105, 91, 114, 94, 86, 95, 105],
        },
        {
          name: 'Services',
          data: [35, 41, 36, 26, 45, 48, 52, 53, 41, 55, 58, 62],
        },
      ],
    },
    quarterlySalesData: {
      quarters: ['Q1', 'Q2', 'Q3', 'Q4'],
      series: [
        {
          name: 'Software',
          data: [156, 175, 189, 220],
        },
        {
          name: 'Hardware',
          data: [262, 290, 299, 286],
        },
        {
          name: 'Services',
          data: [112, 119, 146, 175],
        },
      ],
    },
    colors: ['#4caf50', '#2196f3', '#ff9800'],
  };

  /**
   * Getter for chart data
   */
  @Input()
  get chartData(): SalesChartData {
    return this._chartData;
  }

  /**
   * Setter for chart data
   */
  set chartData(data: SalesChartData) {
    this._chartData = data;
    // If chart is already initialized, update it
    if (this.chartOptions) {
      this.updateChartData();
    }
  }

  /**
   * Initialize the component and set up default chart options
   */
  ngOnInit(): void {
    this.initChartOptions();
  }

  /**
   * Update the chart when inputs change
   */
  ngOnChanges(changes: SimpleChanges): void {
    if (changes['chartData'] && !changes['chartData'].firstChange) {
      this.updateChartData();
    }

    if (changes['selectedPeriod'] && !changes['selectedPeriod'].firstChange) {
      this.updateChartData();
    }
  }

  /**
   * Handles period change and updates the chart accordingly
   * @param event Change event containing the new period value
   */
  onPeriodChange(event: any): void {
    this.selectedPeriod = event.value;
    this.updateChartData();
    this.periodChange.emit(event);
  }

  /**
   * Total sales calculation
   * @returns Total sales across all categories
   */
  getTotalSales(): number {
    const data =
      this.selectedPeriod === 'monthly'
        ? this.chartData.monthlySalesData
        : this.chartData.quarterlySalesData;

    return data.series.reduce((total, series) => {
      return total + series.data.reduce((sum, value) => sum + value, 0);
    }, 0);
  }

  /**
   * Year over year growth percentage
   * @returns Growth percentage
   */
  getYoYGrowth(): number {
    // Mock growth percentage - in a real implementation, this would be calculated from data
    return 24.8;
  }

  /**
   * Updates chart data based on selected time period
   * Made public so child classes can call it when needed
   */
  public updateChartData(): void {
    if (!this.chartOptions) {
      this.initChartOptions();
      return;
    }

    if (this.selectedPeriod === 'monthly') {
      this.chartOptions.series = this.chartData.monthlySalesData.series;
      this.chartOptions.xaxis.categories = this.chartData.monthlySalesData.months;
    } else {
      this.chartOptions.series = this.chartData.quarterlySalesData.series;
      this.chartOptions.xaxis.categories = this.chartData.quarterlySalesData.quarters;
    }

    // Update the chart
    if (this.chart && this.chart.updateOptions) {
      this.chart.updateOptions(this.chartOptions);
    }
  }

  /**
   * Initializes chart options with default configuration
   */
  protected initChartOptions(): void {
    // Use colors from chartData if provided, otherwise use defaults
    const colors = this.chartData.colors || ['#4caf50', '#2196f3', '#ff9800'];

    this.chartOptions = {
      series:
        this.selectedPeriod === 'monthly'
          ? this.chartData.monthlySalesData.series
          : this.chartData.quarterlySalesData.series,
      chart: {
        type: 'bar',
        height: this.height,
        stacked: true,
        toolbar: {
          show: false,
        },
        zoom: {
          enabled: false,
        },
        fontFamily: 'inherit',
      },
      plotOptions: {
        bar: {
          horizontal: false,
          borderRadius: 4,
          columnWidth: '60%',
        },
      },
      colors: colors,
      dataLabels: {
        enabled: false,
      },
      stroke: {
        show: true,
        width: 2,
        colors: ['transparent'],
      },
      xaxis: {
        categories:
          this.selectedPeriod === 'monthly'
            ? this.chartData.monthlySalesData.months
            : this.chartData.quarterlySalesData.quarters,
      },
      yaxis: {
        title: {
          text: 'Revenue ($1,000)',
        },
      },
      fill: {
        opacity: 1,
      },
      tooltip: {
        y: {
          formatter: function (val) {
            return '$' + val + 'k';
          },
        },
      },
      legend: {
        position: 'top',
        horizontalAlign: 'right',
        offsetY: 0,
        fontSize: '14px',
        markers: {
          fillColors: colors,
          shape: 'circle',
        },
      },
      title: {
        text: this.title,
        align: 'left',
        style: {
          fontSize: '16px',
          fontWeight: 'bold',
        },
      },
    };
  }
}

