import { Component, EventEmitter, Input, Output } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'lib-button-1',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './button-1.component.html',
  styleUrl: './button-1.component.scss',
})
export class Button1Component {
  /**
   * The button variant/color
   * Options: 'blue', 'red', 'green', 'yellow', 'primary', 'secondary', 'flat', 'outline'
   */
  @Input() variant:
    | 'blue'
    | 'red'
    | 'green'
    | 'yellow'
    | 'primary'
    | 'secondary'
    | 'flat'
    | 'outline' = 'blue';

  /**
   * Specifies if button has animation effect
   */
  @Input() animate = true;

  /**
   * Button size
   */
  @Input() size: 'small' | 'medium' | 'large' = 'medium';

  /**
   * Whether button has rounded corners
   */
  @Input() rounded = false;

  /**
   * Button is disabled
   */
  @Input() disabled = false;

  /**
   * Type of the button (submit, button, reset)
   */
  @Input() type: 'button' | 'submit' | 'reset' = 'button';

  /**
   * Icon to display (PrimeNG icon name without the pi- prefix)
   */
  @Input() icon = '';

  /**
   * Position of the icon
   */
  @Input() iconPosition: 'left' | 'right' | 'none' = 'none';

  /**
   * Click event emitter
   */
  @Output() clickEvent = new EventEmitter<Event>();

  /**
   * Handles the button click event
   */
  handleClick(event: Event): void {
    if (!this.disabled) {
      this.clickEvent.emit(event);
    }
  }
}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'lib-button-2',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './button-2.component.html',
  styleUrl: './button-2.component.scss',
})
export class Button2Component {
  /**
   * The icon to display (PrimeNG icon name without the pi- prefix)
   */
  @Input() icon = '';

  /**
   * Button text content (used instead of ng-content for better positioning control)
   */
  @Input() text = '';

  /**
   * Position of the icon and content
   */
  @Input() position: 'left' | 'right' = 'left';

  /**
   * Button style variant
   */
  @Input() variant: 'default' | 'arrow' | 'gear' | 'slant' = 'default';

  /**
   * Button color variant
   */
  @Input() color: 'default' | 'blue' | 'red' | 'green' = 'default';

  /**
   * Button size
   */
  @Input() size: 'small' | 'medium' | 'large' = 'medium';

  /**
   * Button is disabled
   */
  @Input() disabled = false;

  /**
   * Type of the button (submit, button, reset)
   */
  @Input() type: 'button' | 'submit' | 'reset' = 'button';

  /**
   * Href attribute for anchor tag
   */
  @Input() href?: string;

  /**
   * Click event emitter
   */
  @Output() clickEvent = new EventEmitter<Event>();

  /**
   * Handles the button click event
   */
  handleClick(event: Event): void {
    if (!this.disabled) {
      this.clickEvent.emit(event);

      // If button has href but we want to handle the click in Angular,
      // we can prevent default navigation
      if (this.href) {
        event.preventDefault();
      }
    }
  }
}

import { CommonModule } from '@angular/common';
import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'storybook-button',
  standalone: true,
  imports: [CommonModule],
  template: ` <button
  type="button"
  (click)="onClick.emit($event)"
  [ngClass]="classes"
  [ngStyle]="{ 'background-color': backgroundColor }"
>
  {{ label }}
</button>`,
  styleUrls: ['./button.css'],
})
export class ButtonComponent {
  /** Is this the principal call to action on the page? */
  @Input()
  primary = false;

  /** What background color to use */
  @Input()
  backgroundColor?: string;

  /** How large should the button be? */
  @Input()
  size: 'small' | 'medium' | 'large' = 'medium';

  /**
   * Button contents
   *
   * @required
   */
  @Input()
  label = 'Button';

  /** Optional click handler */
  @Output()
  onClick = new EventEmitter<Event>();

  public get classes(): string[] {
    const mode = this.primary ? 'storybook-button--primary' : 'storybook-button--secondary';

    return ['storybook-button', `storybook-button--${this.size}`, mode];
  }
}

