      min-height: 6rem;
    }

    .inline-label-container {
      display: flex;
      min-width: 100px;
      max-width: 200px;
      align-items: center;
      min-height: 46px;
    }

    .currency-input-label {
      margin-bottom: 0.5rem;
      font-size: 0.875rem;
      font-weight: 500;
      color: var(--text-color, #495057);

      &.required::after {
        content: "*";
        color: var(--red-500, #f44336);
        margin-left: 0.25rem;
      }

      &.inline-label {
        margin-bottom: 0;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
        display: block;
        width: 100%;
      }
    }

    .currency-input-wrapper {
      position: relative;

      &.has-error {
        ::ng-deep .p-inputnumber .p-inputtext {
          border-color: var(--red-500, #f44336);

          &:focus {
            box-shadow: 0 0 0 1px var(--red-500, #f44336);
          }
        }
      }
    }

    @keyframes errorFade {
      from {
        opacity: 0;
        transform: translateY(-5px);
      }
      to {
        opacity: 1;
        transform: translateY(0);
      }
    }

    .message-container {
      min-height: 1.5rem;
      position: relative;

      .helper-text {
        font-size: 0.75rem;
        color: var(--text-color-secondary, #6c757d);
        animation: errorFade 0.3s ease-in-out;
        display: block;
      }

      .error-text {
        font-size: 0.75rem;
        color: var(--red-500, #f44336);
        animation: errorFade 0.3s ease-in-out;
        display: block;
      }

      .success-text {
        font-size: 0.75rem;
        color: var(--green-500, #4caf50);
        animation: errorFade 0.3s ease-in-out;
        display: block;
      }
    }
  }

  // Additional styling for nested PrimeNG components
  ::ng-deep {
    // Currency Input styling
    .p-inputnumber {
      width: 100%;

      .p-inputtext {
        width: 100%;
        background-color: var(--surface-color);
        transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;
        padding: 0.75rem 1rem;
        font-size: 0.95rem;

        &:enabled:focus {
          border-color: var(--primary-color, #3B82F6);
          box-shadow: 0 0 0 1px var(--primary-color, #3B82F6);
        }

        &:enabled:hover {
          border-color: var(--primary-color, #3B82F6);
        }

        &::placeholder {
          color: #a0aec0;
        }

        &.ng-invalid.ng-dirty {
          border-color: var(--red-500, #f44336);

          &:focus {
            box-shadow: 0 0 0 1px var(--red-500, #f44336);
          }
        }
      }

      // Size variations
      &.small .p-inputtext {
        font-size: 0.875rem;
        padding: 0.5rem 0.75rem;
      }

      &.large .p-inputtext {
        font-size: 1.1rem;
        padding: 0.875rem 1.25rem;
      }

      // Button styling
      .p-inputnumber-button {
        background-color: var(--surface-200);
        color: var(--text-color-secondary);

        &:hover {
          background-color: var(--surface-300);
        }

        &:focus {
          box-shadow: 0 0 0 1px var(--primary-color);
        }
      }

      // Currency specific - ensure proper alignment
      &.p-inputnumber-currency {
        .p-inputtext {
          text-align: right;
        }

        .p-inputnumber-prefix {
          color: var(--text-color);
          margin-right: 0.5rem;
        }

        .p-inputnumber-suffix {
          color: var(--text-color);
          margin-left: 0.5rem;
        }
      }
    }

    // Label styling
    .p-float-label label, .p-ifta-label label {
      &.required::after {
        content: "*";
        color: var(--red-500, #f44336);
        margin-left: 0.25rem;
      }
    }

    // Float label styling
    .p-float-label .p-inputnumber .p-inputtext:focus ~ label,
    .p-float-label .p-inputnumber.p-inputwrapper-focus ~ label,
    .p-float-label .p-inputnumber.p-inputwrapper-filled ~ label {
      top: -0.75rem;
      font-size: 0.75rem;
      color: var(--primary-color, #3B82F6);
    }
  }
}

// Responsive adjustments
@media (max-width: 640px) {
  :host {
    .currency-input-container {
      .inline-label-container {
        min-width: 80px; // Smaller on mobile
      }

      .currency-input-label.inline-label {
        font-size: 0.8rem;
      }
    }

    // Stack label and input for very small screens
    @media (max-width: 480px) {
      .currency-input-container .flex.items-center {
        flex-direction: column;
        align-items: flex-start;

        .inline-label {
          margin-bottom: 0.5rem;
          width: 100%;
          padding-top: 0;
        }
      }
    }
  }
}

