          background-color: var(--primary-color, #3B82F6);
          border-color: var(--primary-color, #3B82F6);
          color: var(--primary-color-text, #ffffff);

          &:hover {
            background-color: var(--primary-600, #2563eb);
            border-color: var(--primary-600, #2563eb);
          }
        }

        &.p-disabled {
          background-color: var(--surface-200, #e9ecef);
          color: var(--text-color-secondary, #6c757d);
          cursor: not-allowed;
          opacity: 0.6;
        }

        &:first-child {
          border-radius: var(--border-radius, 0.375rem) 0 0 var(--border-radius, 0.375rem);
        }

        &:last-child {
          border-radius: 0 var(--border-radius, 0.375rem) var(--border-radius, 0.375rem) 0;
        }

        &:not(:first-child) {
          margin-left: -1px;
        }

        // Only child
        &:only-child {
          border-radius: var(--border-radius, 0.375rem);
        }
      }

      // Size variations
      &.p-selectbutton-sm .p-button {
        padding: 0.375rem 0.75rem;
        font-size: 0.875rem;
      }

      &.p-selectbutton-lg .p-button {
        padding: 0.75rem 1.25rem;
        font-size: 1.125rem;
      }

      // Invalid state
      &.ng-invalid.ng-dirty {
        .p-button {
          border-color: var(--red-500, #f44336);

          &:hover:not(.p-highlight) {
            border-color: var(--red-500, #f44336);
          }
        }
      }
    }
  }
}

// Responsive adjustments
@media (max-width: 640px) {
  :host {
    .select-button-container {
      ::ng-deep {
        .p-selectbutton {
          flex-wrap: wrap;

          .p-button {
            flex: 0 0 auto;
            min-width: 120px;
            margin-bottom: 0.5rem;

            &:not(:last-child) {
              margin-right: 0.5rem;
            }

            &:first-child {
              border-radius: var(--border-radius, 0.375rem);
            }

            &:last-child {
              border-radius: var(--border-radius, 0.375rem);
            }
          }
        }
      }
    }
  }
}

