}
*/

.quill-editor-container {
  display: flex;
  flex-direction: column;
  border: 1px solid #ccc;
  border-radius: 4px;
  background-color: #fff;
  overflow: hidden;

  &.readonly {
    .quill-toolbar {
      display: none;
    }

    .quill-content-area {
      background-color: #f9f9f9;
    }

    .ql-editor {
      border-top: 1px solid #ddd;
    }
  }

  .quill-toolbar {
    border-bottom: 1px solid #ddd;
    background-color: #f8f8f8;
    padding: 8px;

    .ql-formats {
      margin-right: 12px;

      &:last-child {
        margin-right: 0;
      }
    }

    button {
      height: 28px;
      width: 28px;
      display: inline-flex;
      align-items: center;
      justify-content: center;

      &:hover {
        background-color: #e0e0e0;
      }

      &.ql-active {
        background-color: #e3f2fd;
        color: #1976d2;
      }
    }

    .ql-picker {
      &.ql-expanded .ql-picker-options {
        z-index: 10;
      }
    }
  }

  .quill-content-area {
    flex: 1;
    min-height: 150px;

    .ql-editor {
      font-size: 1rem;
      line-height: 1.6;
      padding: 12px;
      min-height: 150px;
      outline: none;

