      a {
        color: #1976d2;
        text-decoration: underline;
      }

      pre.ql-syntax {
        background-color: #23241f;
        color: #f8f8f2;
        overflow: visible;
        padding: 1em;
        border-radius: 4px;
      }
    }
  }
}

// Custom styles for Snow theme to make it more compact
:host ::ng-deep {
  .ql-snow {
    .ql-picker {
      &.ql-size {
        .ql-picker-label::before,
        .ql-picker-item::before {
          content: attr(data-value);
