:host {
  display: block;

  .chart-container {
    height: 350px;
    width: 100%;
  }

  // Loading Spinner
  .spinner-container {
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .spinner {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    border: 3px solid rgba(0, 0, 0, 0.1);
    border-top-color: var(--primary-color, #3B82F6);
    animation: spin 1s infinite linear;
  }

  @keyframes spin {
    to {
      transform: rotate(360deg);
    }
  }

  ::ng-deep {
    // Style customizations for ApexCharts
    .apexcharts-legend-text {
      color: var(--text-color, #333) !important;
    }

    .apexcharts-tooltip {
      background-color: var(--surface-card, #fff) !important;
      border: 1px solid var(--surface-border, #ddd) !important;
      box-shadow: var(--card-shadow, 0 2px 8px rgba(0, 0, 0, 0.1)) !important;

