import {
  Component,
  OnDestroy,
  Input,
  Output,
  EventEmitter,
  ViewEncapsulation,
  ViewChild,
  ElementRef,
  PLATFORM_ID,
  Inject,
  AfterViewInit,
} from '@angular/core';
import { isPlatformBrowser } from '@angular/common';
import { BaseEditor, EditorOptions } from '../base-editor.interface';
import { FormsModule } from '@angular/forms';
import { Subject } from 'rxjs';

declare const tinymce: any;

@Component({
  selector: 'lib-tinymce-editor',
  standalone: true,
  imports: [FormsModule],
  templateUrl: './tinymce-editor.component.html',
  styleUrls: ['./tinymce-editor.component.scss'],
  encapsulation: ViewEncapsulation.None,
})
export class TinyMCEEditorComponent implements OnDestroy, AfterViewInit, BaseEditor {
  @Input() content = '';
  @Input() options: EditorOptions = {
    placeholder: 'Start typing...',
    readonly: false,
    minHeight: '200px',
  };

  @Output() contentChange = new EventEmitter<string>();
  @Output() editorReady = new EventEmitter<any>();
  @Output() focusChange = new EventEmitter<boolean>();

  @ViewChild('editorContainer') editorContainer!: ElementRef;

  editor: any;
  isEditorReady = false;
  isDisabled = false;
  isBrowser: boolean;
  editorId: string;
  private destroy$ = new Subject<void>();
  private loadingPromise: Promise<void> | null = null;

  constructor(@Inject(PLATFORM_ID) private platformId: object) {
    this.isBrowser = isPlatformBrowser(platformId);
    this.editorId = 'tinymce-editor-' + Math.random().toString(36).substring(2, 11);
  }

  ngAfterViewInit(): void {
    if (this.isBrowser) {
      this.loadTinyMCE()
        .then(() => this.initEditor())
        .catch((err) => console.error('Failed to load TinyMCE:', err));
    }
  }

  setContent(content: string): void {
    if (this.editor) {
      this.editor.setContent(content);
      this.content = content;
    }
  }

  getContent(): string {
    return this.editor ? this.editor.getContent() : this.content;
  }

  isValid(): boolean {
    return !!this.editor && this.content.trim().length > 0;
  }

  focus(): void {
    this.editor?.focus();
  }

  blur(): void {
    this.editor?.getBody().blur();
  }

  clear(): void {
    this.editor?.setContent('');
  }

  enable(): void {
    if (this.editor) {
      this.editor.setMode('design');
      this.isDisabled = false;
    }
  }

  disable(): void {
    if (this.editor) {
      this.editor.setMode('readonly');
      this.isDisabled = true;
    }
  }

  insertImage(url: string, title?: string): void {
    this.editor?.insertContent(`<img src="${url}" alt="${title || 'Image'}" />`);
  }

  insertLink(url: string, text?: string): void {
    const selectedText = this.editor?.selection.getContent({ format: 'text' });
    const linkText = text || selectedText || url;
    this.editor?.insertContent(`<a href="${url}">${linkText}</a>`);
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
    if (this.editor) {
      tinymce.remove(this.editor);
      this.editor = null;
    }
  }

  private async loadTinyMCE(): Promise<void> {
    if (this.loadingPromise) {
      return this.loadingPromise;
    }

    this.loadingPromise = new Promise<void>((resolve, reject) => {
      if (typeof tinymce !== 'undefined') {
        resolve();
        return;
      }

      try {
        const script = document.createElement('script');
        script.src = 'https://cdn.tiny.cloud/1/no-api-key/tinymce/6/tinymce.min.js';
        script.referrerPolicy = 'origin';

        script.onload = () => resolve();
        script.onerror = () => reject(new Error('Failed to load TinyMCE editor from CDN'));

        document.head.appendChild(script);
      } catch (err) {
        reject(err);
      }
    });

    return this.loadingPromise;
  }

  private initEditor(): void {
    if (!this.isBrowser || !this.editorContainer) {
      return;
    }

    const editorConfig = {
      target: this.editorContainer.nativeElement,
      height: parseInt(this.options.minHeight || '200', 10),
      menubar: false,
      plugins:
        this.options.customToolbarOptions?.plugins ||
        'advlist autolink lists link image charmap preview anchor ' +
          'searchreplace visualblocks code fullscreen ' +
          'insertdatetime media table code help wordcount',
      toolbar:
        this.options.customToolbarOptions?.toolbar ||
        'undo redo | formatselect | ' +
          'bold italic backcolor | alignleft aligncenter ' +
          'alignright alignjustify | bullist numlist outdent indent | ' +
          'removeformat | help',
      promotion: false,
      branding: false,
      statusbar: false,
      placeholder: this.options.placeholder || 'Start typing...',
      readonly: this.options.readonly || false,
      setup: (editor: any) => {
        this.editor = editor;

        editor.on('Change', () => {
          const content = editor.getContent();
          this.content = content;
          this.contentChange.emit(content);
        });

        editor.on('focus', () => this.focusChange.emit(true));
        editor.on('blur', () => this.focusChange.emit(false));

        editor.on('init', () => {
          if (this.content) {
            editor.setContent(this.content);
          }

          if (this.options.readonly) {
            editor.setMode('readonly');
          }

          this.isEditorReady = true;
          this.editorReady.emit(editor);
        });
      },
    };

    tinymce.init(editorConfig);
  }
}

styleUrlsData.data:
.tinymce-editor-container {
  display: flex;
  flex-direction: column;
  border: 1px solid #ccc;
  border-radius: 4px;
  overflow: hidden;
  position: relative;
  background-color: #fff;

  &.readonly {
    .tox-editor-header {
      display: none;
    }

    .mce-content-body {
      background-color: #f9f9f9;
    }
  }

  .tinymce-content-area {
    min-height: 200px;
  }

  .tinymce-loading {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    background-color: rgba(255, 255, 255, 0.8);
    z-index: 2;

    .loading-spinner {
      width: 40px;
      height: 40px;
      border: 4px solid #f3f3f3;
      border-top: 4px solid #3498db;
      border-radius: 50%;
      animation: spin 1s linear infinite;
      margin-bottom: 8px;
    }

    @keyframes spin {
      0% { transform: rotate(0deg); }
      100% { transform: rotate(360deg); }
    }
  }
}

/* TinyMCE Customizations */
:host ::ng-deep {
  // Main editor container
  .tox-tinymce {
    border: none !important; // Use our container border instead
    width: 100%;
  }

  // Editor toolbar
  .tox-toolbar__primary {
    background-color: #f8f8f8 !important;
    border-bottom: 1px solid #ddd !important;
  }

  // Button states
  .tox-tbtn {
    &:hover {
      background-color: #e0e0e0 !important;
    }

    &.tox-tbtn--enabled {
      background-color: #e3f2fd !important;
      color: #1976d2 !important;
    }
  }

  // Status bar (if enabled)
  .tox-statusbar {
    border-top: 1px solid #ddd !important;
    background-color: #f8f8f8 !important;
  }

  // Editor content styling
  .mce-content-body {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif !important;
    font-size: 1rem !important;
    line-height: 1.6 !important;
    padding: 16px !important;

    p {
      margin-bottom: 1em !important;
    }

    h1, h2, h3, h4, h5 {
      margin-top: 1em !important;
      margin-bottom: 0.5em !important;
      font-weight: 600 !important;
    }

    h1 {
      font-size: 1.5em !important;
    }

    h2 {
      font-size: 1.3em !important;
    }

    h3 {
      font-size: 1.1em !important;
    }

    ul, ol {
      padding-left: 1.5em !important;
      margin-bottom: 1em !important;
    }

    blockquote {
      border-left: 3px solid #ccc !important;
      margin: 0 0 1em !important;
      padding: 0.5em 0 0.5em 1em !important;
      color: #666 !important;
      font-style: italic !important;
    }

    a {
      color: #1976d2 !important;
      text-decoration: underline !important;
    }

    img {
      max-width: 100% !important;
      height: auto !important;
    }

    table {
      border-collapse: collapse !important;
      width: 100% !important;
      margin-bottom: 1em !important;

      td, th {
        border: 1px solid #ddd !important;
        padding: 8px !important;
      }

      th {
        background-color: #f8f8f8 !important;
      }
    }

    pre {
      background-color: #f6f8fa !important;
      border-radius: 3px !important;
      padding: 16px !important;
      overflow: auto !important;
      font-family: monospace !important;
    }

    code {
      background-color: #f6f8fa !important;
      border-radius: 3px !important;
      padding: 2px 4px !important;
      font-family: monospace !important;
    }
  }

  // Hide branding (just in case)
  .tox-statusbar__branding {
    display: none !important;
  }

  // Dialogs
  .tox-dialog {
    border-radius: 4px !important;

    &__title {
      font-size: 1.2em !important;
    }

    &__body-content {
      padding: 16px !important;
    }
  }

  // Placeholder styling
  .mce-content-body[data-mce-placeholder]:not([data-mce-placeholder=""]):not(.mce-visualblocks)::before {
    color: #aaa !important;
    font-style: italic !important;
  }
}

