/* libs/ui-lib/src/lib/wysiwyg-editors/tiptap-editor/tiptap-editor.component.scss */

.tiptap-editor-container {
  display: flex;
  flex-direction: column;
  border: 1px solid #ccc;
  border-radius: 4px;
  overflow: hidden;
  background-color: #fff;

  .tiptap-toolbar {
    display: flex;
    flex-wrap: wrap;
    gap: 4px;
    padding: 8px;
    background-color: #f5f5f5;
    border-bottom: 1px solid #ddd;

    .toolbar-button {
      display: inline-flex;
      align-items: center;
      justify-content: center;
      background: none;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      font-size: 14px;
      height: 32px;
      width: 32px;
      transition: all 0.2s ease;

      &:hover {
        background-color: #e0e0e0;
      }

      &.active {
        background-color: #e3f2fd;
        color: #1976d2;
      }

      &.link-button, &.clear-button {
        width: auto;
        padding: 0 8px;
      }

      i {
        font-size: 16px;
      }
    }

    .toolbar-separator {
      display: inline-block;
      width: 1px;
      height: 24px;
      background-color: #ddd;
      margin: 0 4px;
      align-self: center;
    }
  }

  .tiptap-content-area {
    padding: 12px;
    min-height: 150px;
    outline: none;
    overflow-y: auto;

    &.readonly {
      background-color: #f9f9f9;
      cursor: default;
    }

    /* Editor content styles */
    p {
      margin: 0 0 0.8em;
    }

    h1 {
      font-size: 1.5em;
      margin: 1em 0 0.5em;
    }

    h2 {
      font-size: 1.3em;
      margin: 1em 0 0.5em;
    }

    h3 {
      font-size: 1.1em;
      margin: 1em 0 0.5em;
    }

    ul, ol {
      padding-left: 1.5em;
      margin: 0.5em 0;
    }

    blockquote {
      border-left: 3px solid #ddd;
      margin: 0.5em 0;
      padding-left: 1em;
      color: #666;
    }

    a {
      color: #1976d2;
      text-decoration: underline;
    }

    img {
      max-width: 100%;
      height: auto;
      display: block;
      margin: 0.5em 0;
      border-radius: 4px;
    }

    .ProseMirror-focused {
      outline: none;
    }

    .ProseMirror p.is-editor-empty:first-child::before {
      content: attr(data-placeholder);
      float: left;
      color: #adb5bd;
      pointer-events: none;
      height: 0;
    }
  }
}
